mapper.select:
select
  id, code, name, parentId, level, type, servicestate, delete_flag, region
from sys_area


        and id = #{id}


        and code = #{code}


        and name = #{name}


        and parentId = #{parentid}


        and level = #{level}


        and type = #{type}


        and servicestate = #{servicestate}


        and delete_flag = #{deleteFlag}


        and region = #{region}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from sys_area


        and id = #{id}


        and code = #{code}


        and name = #{name}


        and parentId = #{parentid}


        and level = #{level}


        and type = #{type}


        and servicestate = #{servicestate}


        and delete_flag = #{deleteFlag}


        and region = #{region}
